#define S(a,b)strcmp(a,b)
main(t){char a[][21]={"zero","one","two","three","four","five","six","seven","eight","nine","ten","eleven","twelve","thirteen","fourteen","fifteen","sixteen","seventeen","eighteen","nineteen","twenty"},b[][20]={"twenty","thirty","forty","fifty","sixty","seventy","eighty","ninety"};scanf("%d%*c",&t);while(t--){char c,w[100][50]={0};int i=0,j=0,k=0,n=0,p=0;while(scanf("%c",&c)&&c!='\n'){if(c==' ')w[j++][k]=0,k=0;else w[j][k++]=c;}for(;i<=j;i++){if(!S(w[i],"thousand"))n+=p*1000,p=0;if(!S(w[i],"hundred"))n+=p*100,p=0;for(k=0;k<20;k++)p+=S(w[i],a[k])?0:k;for(k=0;k<8;k++)p+=S(w[i],b[k])?0:10*k+20;}printf("%d\n",n+p);}}
